// begin change language 

let arrLang = {
  'en': {
    'about': 'About me',
    'skills': 'Skills',
    'portfolio': 'Portfolio',
    'reviews': 'reviews',
    'contact': 'Contact me',
    'name': 'Tatyana Pilke',
    'html': 'web debeloper',
    'order': 'Order',
  }
}

$(function() {
  $('.translate').click(function() {
    let lang = $(this).attr('id');

    $('.lang').each(function(index, item) {
      $(this).text(arrLang[lang][$(this).attr('key')]);
    });
  });
});




// end change language  